import ShareOfCompaniesComponent from "./ShareOfCompaniesComponents";
import useStore from '../../../stores/useStore';
import shallow from 'zustand/shallow';
import { useEffect } from "react";

const getState = state => [
    state.companyShares,
    state.getCompanyShares
];
const ShareOfCompanies = () => {
    const [companyShares, setCompanyShares, getCompanyShares] = useStore(getState, shallow);
    useEffect(()=>{
        getCompanyShares();
    }, [getCompanyShares]);
    const args = {
        companyShares,
        setCompanyShare: share => {
            setCompanyShares([{property: "share", value: share}])
        },
        setCompanyName: name => {
            setCompanyShares([{property: "name", value: name}])
        }
    }

    return <ShareOfCompaniesComponent {...args} />
}

export default ShareOfCompanies;
