    if(!Number.isNaN(key)){
        const response = await axios.get("http://192.168.14.33/otcs/llisapi.dll?func=ll&objId=126689&objAction=RunReport&key"+key);
        const data = response.data;
        data.pop();
        set({
            financialData: {
                active: data[0].CEMIAKTIVLER,
                capital: data[0].CEMIKAPITAL,
