import {useState} from 'react';
import PropTypes from 'prop-types';
const ShareOfCompaniesComponent = (props) => {
  const shareList = [];
  for(var i=0; i<props.companyShares.length;i++){
    shareList.push(props.companyShares[i].share);
  }
  const [Cshare, setCShare] = useState(shareList);
  const addShare = () => {
    console.log(Cshare);
  }
    return (
      <main className="content">
        <div className="content-header">
          <button className="content-header--button">
            View history of changes
            <i className="fa-solid fa-arrow-right-from-bracket content-header--icon"></i>
          </button>
        </div>
        <form className="content-form">
        {props.companyShares.map((x, id) => {
            return (
              <div className="form-group row" key={id}>
                {/* <label htmlFor="comapny" className="col-sm-3 col-form-label">
                  {x.name}
                </label> */}
                <div className="col-sm-3 col-form-label">
                  <input
                    value={x.name}
                    onChange={((e) => props.setCompanyShare(e.target.value))}
                    type="text"
                    className="form-control"
                    id="company"
                  />
                </div>

                <div className="col-sm-2 flex-div">
                  <input
                    value={x.share}
                    onChange={((e) => props.setCompanyShare(e.target.value))}
                    type="text"
                    className="form-control"
                    id="company"
                  />
                  <span className="col-sm-1">
                    <i className="fa-solid fa-percent"></i>
                  </span>
                  <button className="removebtn1">
                    x
                  </button>
                </div>
              </div>
            );
        })} 
          <div className="form-group row">
            <div className="col-sm-10">
              <button
                type="button"
                className="col-sm-1 add-button"
                id="add-member"
                onClick={addShare}
              >
                Add
                <i
                  className="fa-solid fa-plus"
                  style={{ marginLeft: "10px" }}
                ></i>
              </button>
            </div>
          </div>
          <div className="form-group row">
            <div className="col-sm-12 button-right">
              <button type="button" className="btn-save">
                Send
              </button>
              <button type="button" className="btn-reset">
                Reset
              </button>
            </div>
          </div>
        </form>
      </main>
    );
};
ShareOfCompaniesComponent.propTypes = {
  companyShares: PropTypes.array,
};

export default ShareOfCompaniesComponent;
