import BoardMembersComponent from "./BoardMembersComponent";
import useStore from '../../../stores/useStore';
import shallow from 'zustand/shallow';
import { useEffect, useState } from "react";

const getState = state => [
    state.directorList,
    state.getDirectors,
    state.representativeList,
    state.getRepresentatives,
    state.initWorkflow
];

const BoardMembers = () => {
    const [directorList, getDirectors, representativeList, getRepresentatives, initWorkflow] = useStore(getState, shallow);

    useEffect(()=>{
        getDirectors();
    }, [getDirectors]);

    useEffect(()=>{
        getRepresentatives();
    }, [getRepresentatives]);

    const [names, setNames] = useState(directorList);
    const [namesR, setNamesR] = useState(props.representativeList);

    useEffect(() => {
      setNames(props.directorList);
    }, [props.directorList]);
    useEffect(() => {
      setNamesR(props.representativeList);
    }, [props.representativeList]);

    
    const args = {
        directorList,
        representativeList,  
        onSubmitForm: () => {
            initWorkflow();
        }
    };
    return <BoardMembersComponent {...args} />
}

export default BoardMembers;
